// Renewal reminder system
const memberships = $input.all();
const today = new Date();
const reminders = [];

for (const membership of memberships) {
  const data = membership.json;
  const endDate = new Date(data.end_date);
  const daysUntilExpiry = Math.ceil((endDate - today) / (1000 * 60 * 60 * 24));

  // Determine reminder stage based on days until expiry
  let reminderStage = null;
  let priority = 'normal';
  let templateType = 'renewal';

  if (daysUntilExpiry <= 0) {
    // Expired
    reminderStage = 'expired';
    priority = 'urgent';
    templateType = 'expired';
  } else if (daysUntilExpiry <= 7) {
    // Final reminder - 7 days or less
    reminderStage = 'final';
    priority = 'urgent';
    templateType = 'final_reminder';
  } else if (daysUntilExpiry <= 30) {
    // Second reminder - 30 days
    reminderStage = 'second';
    priority = 'high';
    templateType = 'second_reminder';
  } else if (daysUntilExpiry <= 60) {
    // First reminder - 60 days
    reminderStage = 'first';
    priority = 'normal';
    templateType = 'first_reminder';
  } else if (daysUntilExpiry <= 90) {
    // Early renewal offer - 90 days
    reminderStage = 'early_offer';
    priority = 'low';
    templateType = 'early_renewal';
  }

  if (reminderStage) {
    // Check if reminder was already sent recently
    const lastReminderDate = new Date(data.last_renewal_reminder || '2000-01-01');
    const daysSinceLastReminder = Math.ceil((today - lastReminderDate) / (1000 * 60 * 60 * 24));

    // Only send reminder if enough time has passed
    let shouldSend = false;
    switch (reminderStage) {
      case 'expired':
        shouldSend = daysSinceLastReminder >= 3; // Every 3 days for expired
        break;
      case 'final':
        shouldSend = daysSinceLastReminder >= 2; // Every 2 days for final
        break;
      case 'second':
        shouldSend = daysSinceLastReminder >= 7; // Weekly for second
        break;
      case 'first':
        shouldSend = daysSinceLastReminder >= 14; // Bi-weekly for first
        break;
      case 'early_offer':
        shouldSend = daysSinceLastReminder >= 30; // Monthly for early offer
        break;
    }

    if (shouldSend) {
      reminders.push({
        membership_id: data.id,
        contact_id: data.contact_id,
        display_name: data.display_name,
        email: data.email,
        membership_type: data.membership_type,
        start_date: data.start_date,
        end_date: data.end_date,
        days_until_expiry: daysUntilExpiry,
        reminder_stage: reminderStage,
        priority: priority,
        template_type: templateType,
        membership_fee: data.membership_fee || 0,
        payment_method: data.payment_method || 'not_set',
        auto_renew: data.auto_renew || false,
        renewal_url: `${$vars.WEBSITE_URL}/mitgliedschaft/erneuern?id=${data.id}&token=${data.renewal_token}`,
        phone: data.phone,
        address: data.address
      });
    }
  }
}

// Sort by priority and days until expiry
const priorityOrder = { urgent: 1, high: 2, normal: 3, low: 4 };
reminders.sort((a, b) => {
  if (priorityOrder[a.priority] !== priorityOrder[b.priority]) {
    return priorityOrder[a.priority] - priorityOrder[b.priority];
  }
  return a.days_until_expiry - b.days_until_expiry;
});

return reminders.map(reminder => ({ json: reminder }));
